import React from "react"
import './App.css';
import Header from './Components/Header'
import { Col, Row, Button, Form, FormGroup, Label, Input } from 'reactstrap';



class App extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:'',
            pin:''
        }

        this.handleChange=this.handleChange.bind(this)
    }

    handleChange(changeObject){
        this.setState(changeObject)
        console.log(changeObject)
    }
    create(e) {
        // add entity - POST
        e.preventDefault();
        // creates entity
        fetch("https://fairestdb.p.rapidapi.com/friend/friendModel", {
            "method": "POST",
            "headers": {
                "x-rapidapi-host": "fairestdb.p.rapidapi.com",
                "x-rapidapi-key": API_KEY,
                "content-type": "application/json",
                "accept": "application/json"
            },
            "body": JSON.stringify({
                name: this.state.name,
                notes: this.state.notes
            })
        })
            .then(response => response.json())
            .then(response => {
                console.log(response)
            })
            .catch(err => {
                console.log(err);
            });
    }

    render() {
        return(
            <div align="center">
                <Form>
                    <Row form>
                        <Col md={3}>
                            <FormGroup>
                                <Label for="Account Number">Account Number</Label>

                                <Input type="text" name="accountNumber" id="accountnumber" placeholder="Enter account Number"
                                       value={this.state.accountNumber}
                                       onChange={(e) => this.handleChange({ accountNumber: e.target.value })} />

                            </FormGroup>
                        </Col>
                        <Col md={2}>
                            <FormGroup>
                                <Label for="examplePassword">Pin</Label>
                                <Input type="password" name="pin" id="atmPin" placeholder="Enter 4 digit Pin"
                                       value={this.state.pin}
                                       onChange={(e) => this.handleChange({ pin: e.target.value })} />
                            </FormGroup>
                        </Col>
                    </Row>

                    <Button onClick={(e) => this.update(e)} >Sign in</Button>
                </Form>
            </div>
        );
    }
}
export default App;
