import React from 'react';
import {fetchCreditDetailsApi} from "./Utils";
import {Table} from "reactstrap";

class CreditDetails extends React.Component{

    constructor(props) {
        super(props);
        this.state={
            accountNumber:window.localStorage.getItem('accountNumber'),
            records:[],
            date:props.date

        }
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }
    handleChange(change){
        this.setState(change)
    }

    handleSubmit(event){

        event.preventDefault();
    }
    componentDidMount() {

        fetchCreditDetailsApi(this.state.accountNumber).then((data) =>{
            console.log(data)
            this.setState({records:data.data})
        }).catch((error) =>{
            console.log(error)
        })

    }



    render()
    {
        var records=this.state.records.map(function (i){
            return(
                <tr>
                    <td>{i.time}</td>
                    <td>{i.transDetails}</td>
                    <td>{`₹ ${i.amount}`}</td>
                    <td>{`₹ ${i.totalBalance}`}</td>
                </tr>
            )
        })

        var creditDetails = <Table bordered>
            <thead>
            <tr>
                <th>Date</th>
                <th >Details </th>
                <th> Credited </th>
                <th> Total balance</th>
            </tr>
            </thead>

            <tbody>
            {records}
            </tbody>
        </Table>;


        return(
            <div>
                {creditDetails}
            </div>


        );
    }

}

export default CreditDetails;
