import React from 'react';
import {fetchTransactionDetailsApi} from "./Utils";
import {Table} from "reactstrap";

class TransactionDetails extends React.Component{

    constructor() {
        super();
        this.state={
            accountNumber:window.localStorage.getItem('accountNumber'),
            records:[],
            option:'all'

        }
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }
    handleChange(change){
        console.log(this.state.option)
        this.setState(change)
    }

    handleSubmit(event){

        event.preventDefault();
    }
    componentDidMount() {

            fetchTransactionDetailsApi(this.state.accountNumber).then((data) =>{
                console.log(data)
                this.setState({records:data.data})
            }).catch((error) =>{
                console.log(error)
            })

    }

    renderSwitch(){
        switch(this.state.option) {
            case "Show all":   return all;
            case "Date": return all;
            case "Show by Credit":  return credit;
            default:      return credit;
        }
    }

    render()
    {

        var recordsList=this.state.records.map(function (i){
            return(
                <tr>
                    <td>{i.time}</td>
                    <td>{i.transDetails}</td>
                    <td>{i.debit==='debit'?`₹ ${i.amount}`:"-"}</td>
                    <td>{i.credit==='credit'?`₹ ${i.amount}`:"-"}</td>

                    <td>{`₹ ${i.totalBalance}`}</td>
                </tr>
            )
        })

        var all = <Table bordered>
            <thead>
            <tr>
                <th>Date</th>
                <th >Details </th>
                <th> Debit </th>
                <th> Credit </th>
                <th> Total balance</th>
            </tr>
            </thead>

            <tbody>
            {recordsList}
            </tbody>
        </Table>;
        var creditRecords=this.state.records.map(function (i){
            if(i.credit==='credit'){
                return (
                    <tr>
                        <td>{i.time}</td>
                        <td>{i.transDetails}</td>

                        <td>{`₹ ${i.amount}`}</td>

                        <td>{`₹ ${i.totalBalance}`}</td>
                    </tr>
                )
            }

        })
        var credit= <Table bordered>
            <thead>
            <tr>
                <th>Date</th>
                <th >Details </th>
                <th> Credit </th>
                <th> Total balance</th>
            </tr>
            </thead>

            <tbody>
            {creditRecords}
            </tbody>
        </Table>;
        var show= renderSwitch()
        return(
            <div>
                {show}
                <form onSubmit={this.handleSubmit} style={{marginLeft:"80vw"}}>
                    <select value={this.state.option} onChange={(e) => this.handleChange({option: e.target.value})} >
                        <option>Show all</option>
                        <option>Date</option>
                        <option>Show by Credit</option>
                        <option>Show by Debit</option>
                    </select>
                    <button >Submit</button>
                </form>
            </div>


        );
    }

}

export default TransactionDetails;
