import '../css/Home.css';

import SignIn from "./SignIn";
import Header from "./Header";

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            localStorage:false
        }
    }
    componentDidMount() {
        if(window.localStorage.getItem('logged')==='true'){
            this.setState({localStorage:!localStorage})
        }
    }

    render() {

        let home=<SignIn/>
        if(window.localStorage.getItem('logged')==="true")
        {
            home=<Header/>
        }
        return (
            <div>
                {this.state.localStorage?<Header/>:<SignIn/>}
            </div>

            );
        }
    }

    export default Home
