import React from 'react'
import {ToastContainer} from "react-toastify";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Radio from "@material-ui/core/Radio";
import Form from 'react-bootstrap/Form'
import Button from "@material-ui/core/Button";

class Transfer extends React.Component{
    constructor() {
        super();
        this.state={
            toAccountNumber:''
        }
    }

    render() {

        return(
            <div>
                <ToastContainer/>




                                <Form style={{margin: 'auto',
                                    width: '50%',
                                    height:'30%',
                                    border: '5px solid #FFFF00',
                                    paddingTop: '80px'}}>
                                    <Form.Group controlId="formBasicEmail">
                                        <Form.Label>Account Number</Form.Label>
                                        <Form.Control type="email" placeholder="Enter email" />
                                        <Form.Text className="text-muted">
                                            We'll never share your email with anyone else.
                                        </Form.Text>
                                    </Form.Group>

                                    <Form.Group controlId="formBasicPassword">
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control type="password" placeholder="Password" />
                                    </Form.Group>
                                    <Form.Group controlId="formBasicCheckbox">
                                        <Form.Check type="checkbox" label="Check me out" />
                                    </Form.Group>
                                    <Button variant="primary" type="submit">
                                        Submit
                                    </Button>
                                </Form>

                                {/*<text style={{fontSize:"30px",margin:'80px',fontFamily:'Monospace',color:'#090809'}}>Transfer Money</text>*/}
                                {/*<br/>*/}

                                {/*<input  style={{marginTop:"20px",marginLeft:'90px',paddingLeft:'20px',width: "60%"}} type="number" value={this.state.balance}*/}
                                {/*        onChange={(e) => this.handleChange({ balance: e.target.value })} />*/}
                                {/*<br/>*/}
                                {/*<Button  style={{marginTop:"20px",marginLeft:'160px',width:"30%"}} variant="contained" onClick={this.handleClick}>Submit</Button>*/}



            </div>

        )
    }
}

export default Transfer;
