import React from "react"
import {Row, Card, Col, CardTitle} from "reactstrap";
import {fetchUserDetailsApi} from "./Utils.js";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";

class Profile extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:"919554630599",
            balance:''
        }

    }

    componentDidMount() {
        fetchUserDetailsApi(this.state.accountNumber).then((data) =>{
                if(data){
                    console.log(data)
                    this.setState({balance:data.data.balance})
                }
                else{
                    alert("Somethings Wrong")
                }

            }
        );
    }



    render()
    {

        return (

            <Container style={{paddingTop:40}}>

                <Grid container spacing={4} justify="center" >
                    <Grid item xs >
                        <Paper style={{height:150,width:400,textAlign:'center',paddingTop:'30px',background:'#eeedee'}}  >
                            <text style={{fontSize:"25px",fontFamily:'Monospace',color:'#090809'}}>Account Number</text>
                            <br/>
                            <text style={{fontSize:"25px",fontFamily:'Monospace' ,color:'#090809'}}>{this.state.accountNumber}</text>

                        </Paper>

                    </Grid>
                    <Grid item xs>
                        <Paper style={{height:150,width:400,textAlign:'center',paddingTop:'30px',background:'#eeedee'}}  >
                            <text style={{fontSize:"25px",fontFamily:'Monospace',color:'#090809'}}>Total Balance</text>
                            <br/>
                            <text style={{fontSize:"25px",fontFamily:'Monospace',color:'#090809'}}>â‚¹ {this.state.balance}</text>

                        </Paper>
                    </Grid>


                </Grid>

            </Container>

        );
    }
}
export default Profile;
