import axios from "axios";
import {toast} from "react-toastify";

export async function fetchUserDetails (accountNumber){
    try{
        let response= await axios.get(`http://localhost:8080/details?accountNumber=${accountNumber}`)
        console.log(response);
        return response;
    }
    catch(error) {
        console.log(error);
        return error;
    };
}

