import React from "react"
import {fetchUserDetailsApi} from "./Utils.js";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import 'antd/dist/antd.css';
import { Card } from 'antd';

class Profile extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            accountNumber:window.localStorage.getItem('accountNumber'),
            balance:'',
            aadhaarNo:'',
            name:'',
            panNo:'',
            checkBalance:this.props.checkBalance

        }

    }

    componentDidMount() {

        fetchUserDetailsApi(this.state.accountNumber).then((data) =>{
                    console.log(data)
                    this.setState({balance:data.data.balance})
                    this.setState({aadhaarNo:data.data.aadhaarNo})
                    this.setState({name:data.data.name})
                    this.setState({panNo:data.data.panNo})
            }).catch((error) =>{
            console.log(error)
        });
    }



    render()
    {

        let view=(this.state.checkBalance?<Card  style={{ width: 300,marginLeft:'10px',marginTop:'10px',borderStyle:'dashed',borderWidth:'thick',borderColor:'grey',fontSize:'20px' }}>

            <p style={{fontSize:'30px'}}>Total Balance: {`₹${this.state.balance}`}</p></Card>:<Card  style={{ width: 400,marginLeft:'10px',marginTop:'10px',borderStyle:'dashed',borderWidth:'thick',borderColor:'grey',fontSize:'20px' }}>
            <p>Account Number: {this.state.accountNumber}</p>
            <p>Name : {this.state.name}</p>
            <p>Aadhaar No : {this.state.aadhaarNo}</p>
            <p>PAN No: {this.state.panNo}</p>

        </Card>)

        return (
            <div>

                {view}
            </div>


        );
    }
}
export default Profile;
