import axios from "axios";
import {toast} from "react-toastify";

export async function fetchUserDetails (accountNumber){
    try{
        let response= await axios.get(`http://localhost:8080/details?accountNumber=${accountNumber}`)
        console.log(response);
        return response;
    }
    catch(error) {
        console.log(error);
        return error;
    };
}

export async function transaction (accountNumber,balance){

    const body=JSON.stringify({
        accountNumber:this.state.accountNumber,
        balance:this.state.balance
    });


    const config = { headers: {'Content-Type': 'application/json'} };
    const path=this.state.pathName

    axios.put(  `http://localhost:8080${path}`,body,config)
        .then(function (response) {
            if(response.data==="Success")
            {
                toast.success("Transaction successful");
                console.log(response);
            }
            else{
                toast.error("Transaction Failed");

                console.log(response);
            }
        })
        .catch(function (error) {
            console.log(error);
        });
}
