import React from "react"
import axios from 'axios';
import {Row, Card, Col, CardTitle, CardText} from "reactstrap";


class Profile extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:"102",
            balance:0
        }

    }

    // componentDidMount() {
    //     var self=this.state
    //     axios.get('http://localhost:8080/details?accountNumber=102')
    //         .then(function (response) {
    //             self.setState({balance:response.data.balance})
    //             console.log(response);
    //         })
    //         .catch(function (error) {
    //             console.log(error);
    //         });
    // }



    render()
    {

        var self=this;
        const data= axios.get(`http://localhost:8080/details?accountNumber=100`)
        self.setState({balance:data.data.balance})
        return (
            <div>
                <Row>
                    <Col sm="2">
                        <Card body>
                            <CardTitle tag="h5">Account Number:{this.state.accountNumber}</CardTitle>
                            <CardTitle tag="h5">Balance:{this.state.balance}</CardTitle>
                        </Card>
                    </Col>

                </Row>

            </div>




        );
    }
}
export default Profile;
