import React from 'react';
import {fetchTransactionDetailsApi} from "./Utils";
import {Table} from "reactstrap";
import CreditDetails from "./CreditDetails";
import DebitDetails from "./DebitDetails";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';

class TransactionDetails extends React.Component{

    constructor() {
        super();
        this.state={
            accountNumber:window.localStorage.getItem('accountNumber'),
            records:[],
            option:'all',
            startDate:new Date()

        }
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }
    handleChange(e){
        if(e.target.value==='Date'){
            <DatePicker selected={this.state.startDate}  onChange={(e) => this.handleChange({startDate: e})} />
            return;
        }
        this.setState({option:e.target.value})
    }

    handleSubmit(event){

        event.preventDefault();
    }
    componentDidMount() {

            fetchTransactionDetailsApi(this.state.accountNumber).then((data) =>{
                console.log(data)
                this.setState({records:data.data})
            }).catch((error) =>{
                console.log(error)
            })

    }



    render()
    {


        var recordsList=this.state.records.map(function (i){
            return(
                <tr>
                    <td>{i.time}</td>
                    <td>{i.transDetails}</td>
                    <td>{i.debit==='debit'?`₹ ${i.amount}`:"-"}</td>
                    <td>{i.credit==='credit'?`₹ ${i.amount}`:"-"}</td>

                    <td>{`₹ ${i.totalBalance}`}</td>
                </tr>
            )
        })

        var show = <Table bordered>
            <thead>
            <tr>
                <th>Date</th>
                <th >Details </th>
                <th> Debit </th>
                <th> Credit </th>
                <th> Total balance</th>
            </tr>
            </thead>

            <tbody>
            {recordsList}
            </tbody>
        </Table>;
        if(this.state.option==='Show by Credit')
            show=<CreditDetails/>
        if(this.state.option==='Show by Debit')
            show=<DebitDetails/>
        return(
            <div>
                {show}
                    <select value={this.state.option} onChange={this.handleChange} >
                        <option>Show all</option>
                        <option>Date</option>
                        <option>Show by Credit</option>
                        <option>Show by Debit</option>
                    </select>



            </div>


        );
    }

}

export default TransactionDetails;
