import React from "react";
import {Button, Card, CardTitle, Col, Row} from "reactstrap";
import {toast, ToastContainer} from "react-toastify";
import {transactionApi} from "./Utils";
import 'react-toastify/dist/ReactToastify.css';
import {Divider} from "antd";

class Transaction extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            accountNumber:"102",
            balance:'',
            mode:props.mode
        }
        this.handleChange=this.handleChange.bind(this)
        this.handleClick=this.handleClick.bind(this)
    }

    handleChange(change){
        this.setState(change)
    }

    handleClick(e){


        transactionApi(this.state.accountNumber,this.state.balance,this.state.mode).then((data) =>{
            console.log(data)
            if(data.data==="Success"){
                toast.success("Transaction successful");
                console.log(data);
            }
            else{
                toast.error("Transaction Failed");
                console.log(data);
            }
            this.setState({balance:''})
        })


    }



    render() {


        return(

            <div>
                <ToastContainer/>
                <Row style={{minHeight: "280px",
                    padding: "24px",
                    background: "#fff"}}>
                    <Divider orientation="left">Responsive</Divider>
                    <Col sm="3">
                        <Card body>
                            <CardTitle tag="h5">{this.props.mode==='deposit'?"Deposit":"Withdraw"} Money</CardTitle>
                            <input type="text" value={this.state.balance}
                                   onChange={(e) => this.handleChange({ balance: e.target.value })} />
                            <Button style={{color:"white",background:"blue"}} onClick={this.handleClick}>{this.props.mode==='deposit'?"Deposit":"Withdraw"}</Button>
                        </Card>

                    </Col>
                </Row>
            </div>
        );
    }
}
export default Transaction;
