import React from "react";

import {toast, ToastContainer} from "react-toastify";
import {transactionApi} from "./Utils";
import 'react-toastify/dist/ReactToastify.css';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Button from '@material-ui/core/Button';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

class Transaction extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:"919554630599",
            balance:'',
            mode:'Withdraw'
        }
        this.handleChange=this.handleChange.bind(this)
        this.handleClick=this.handleClick.bind(this)
    }

    handleChange(change){
        this.setState(change)
    }

    handleClick(e){

        var mode=(this.state.mode?'deposit':'withdraw');

        transactionApi(this.state.accountNumber,this.state.balance,mode).then((data) =>{
            console.log(data)
            if(data.data==="Success"){
                toast.success("Transaction successful");
                console.log(data);
            }
            else{
                toast.error("Transaction Failed");
                console.log(data);
            }
            this.setState({balance:''})
        })

        this.setState({mode:!this.state.mode})

    }



    render() {



        return(

            <Container style={{paddingTop:40}}>

                <Grid container justify="center" >
                    <Grid item >
                        <Paper style={{height:300,width:400,paddingTop:'30px',background:'#f8f8f8'}}  >
                            <text style={{fontSize:"25px",marginLeft:'50px',fontFamily:'Monospace',color:'#090809'}}>Money Transaction</text>
                            <br/>
                            <RadioGroup style={{paddingLeft:'80px'}} aria-label="mode"  value={this.state.mode} onChange={this.handleChange}>
                                <FormControlLabel value="Withdraw" control={<Radio />} label="Withdraw" />
                                <FormControlLabel value="Deposit" control={<Radio />} label="Deposit" />

                            </RadioGroup>
                            <input  style={{marginTop:"20px",paddingLeft:'0px'}} type="text" value={this.state.balance}
                                   onChange={(e) => this.handleChange({ balance: e.target.value })} />
                            <br/>
                            <Button  style={{marginTop:"20px"}} variant="contained" onClick={this.handleClick}>Deposit</Button>
                        </Paper>

                    </Grid>



                </Grid>

            </Container>



        );
    }
}
export default Transaction;
