 import React from 'react';
import { fetchTransactionDetailsApi} from "./Utils";
import {Table} from "reactstrap";

class AllDetails extends React.Component{

    constructor(props) {
        super(props);
        this.state={
            accountNumber:window.localStorage.getItem('accountNumber'),
            records:[],
            date:this.props.date

        }
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }
    handleChange(change){
        this.setState(change)
    }

    handleSubmit(event){

        event.preventDefault();
    }
    componentDidMount() {

        fetchTransactionDetailsApi(this.state.accountNumber,this.state.date).then((data) =>{
            console.log(data)
            this.setState({records:data.data})
        }).catch((error) =>{
            console.log(error)
        })

    }



    render()
    {
        var records=this.state.records.map(function (i){
            return(
                <tr>
                    <td>{i.time}</td>
                    <td>{i.transDetails}</td>
                    <td>{i.debit==='debit'?`₹ ${i.amount}`:"-"}</td>
                    <td>{i.credit==='credit'?`₹ ${i.amount}`:"-"}</td>

                    <td>{`₹ ${i.totalBalance}`}</td>
                </tr>
            )
        })


        return(
            <div>
                <Table bordered>
                    <thead>
                    <tr>
                        <th>Date</th>
                        <th >Details </th>
                        <th> Debited </th>
                        <th> Credit </th>
                        <th> Total balance</th>
                    </tr>
                    </thead>
                    <tbody>
                    {records}
                    </tbody>
                </Table>
            </div>


        );
    }

}

export default AllDetails;
