import React from "react"
import './App.css';
import Header from './Components/Header'
import { Col, Row, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';


class App extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:'',
            pin:'',
            logged:null
        }

        this.handleChange=this.handleChange.bind(this)

    }




    handleChange(changeObject){
        this.setState(changeObject)
        console.log(changeObject)
    }



    render()
    {
        let login=this.state.logged ?
            <Form>
                <FormGroup>
                    <Label for="exampleEmail">Welcome to up.assist</Label>
                    <Input type="text" placeholder="Enter your acccount number" value={this.state.accountNumber}
                           onChange={(e) => this.handleChange({ accountNumber: e.target.value })}/>
                </FormGroup>
                <Button o onClick={() => {
                    window.location = "/upassist"
                }}>Submit</Button>
            </Form> : null;
        return (

            <div>
                {login}
                <Header/>
            </div>


        );
    }
}
export default App;
