import React from "react"
import {Row, Card, Col, CardTitle} from "reactstrap";
import {fetchUserDetails} from "./Utils.js";

class Profile extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:"102",
            balance:''
        }
        this.handleChange=this.handleChange.bind(this)

    }
    handleChange(change){
        this.setState(change)
    }

    componentDidMount() {
        var amount='';
        fetchUserDetails(this.state.accountNumber).then((data) => {
                    console.log(data)
                    amount=data.data.balance
            });
        this.handleChange({balance:amount})

    }


    render()
    {

        return (
            <div>
                <Row style={{paddingTop: '50px',paddingLeft:'50px'}} >
                    <Col sm="3">
                        <Card body>
                            <CardTitle tag="h3">Account Number:{this.state.accountNumber}</CardTitle>
                            <CardTitle tag="h3">Balance:{this.state.balance}</CardTitle>
                        </Card>
                    </Col>

                </Row>

            </div>

        );
    }
}
export default Profile;
