import React from "react"
import './App.css';
import Header from './Components/Header'
import { Col, Row, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';


class App extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:'',
            pin:'',
            logged:null
        }

        this.handleChange=this.handleChange.bind(this)
        this.create=this.create.bind(this)
    }

    handleChange(changeObject){
        this.setState(changeObject)
        console.log(changeObject)
    }
    create(e) {

        e.preventDefault();
        axios.post('http://localhost:8080/addCred', {
            accountNumber:this.state.accountNumber
        })
            .then(function (response) {
                console.log(response.data);
                this.setState({logged:response.data})
                console.log(response);
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render()
    {

    let loginView= !this.state.logged ?
        <div align="center">
            <Form>
                <Row form>
                    <Col md={3}>
                        <FormGroup>
                            <Label for="Account Number">Account Number</Label>

                            <Input type="text" name="accountNumber" id="accountnumber" placeholder="Enter account Number"
                                   value={this.state.accountNumber}
                                   onChange={(e) => this.handleChange({ accountNumber: e.target.value })} />

                        </FormGroup>
                    </Col>
                    <Col md={2}>
                        <FormGroup>
                            <Label for="examplePassword">Pin</Label>
                            <Input type="password" name="pin" id="atmPin" placeholder="Enter 4 digit Pin"
                                   value={this.state.pin}
                                   onChange={(e) => this.handleChange({ pin: e.target.value })} />
                        </FormGroup>
                    </Col>
                </Row>

                <Button onClick={(e) => this.create(e)} >Sign in</Button>
            </Form>
        </div> :
        <Header/>

        return (
            <div>
                {loginView}
            </div>


        );
    }
}
export default App;
