import React from 'react'
import {ToastContainer} from "react-toastify";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Radio from "@material-ui/core/Radio";
import Form from 'react-bootstrap/Form'
import Button from "@material-ui/core/Button";

class Transfer extends React.Component{
    constructor() {
        super();
        this.state={
            toAccountNumber:''
        }
    }

    render() {

        return(
            <div>
                <ToastContainer/>
                <Container style={{paddingTop:40}}>

                    <Grid container justify="center" >
                        <Grid item >
                            <Paper style={{height:400,width:600,paddingTop:'30px',background:'#f8f8f8'}}  >
                                <Form style={{margin: 'auto',
                                    width: '60%',

                                    padding: '10px'}}>
                                    <Form.Group >
                                        <Form.Label>Account Number</Form.Label>
                                        <Form.Control type="text" placeholder="Enter payee Account Number" />
                                        <Form.Text className="text-muted">
                                            *required
                                        </Form.Text>
                                    </Form.Group>

                                    <Form.Group >
                                        <Form.Label>Amount</Form.Label>
                                        <Form.Control type="text" placeholder="Enter amount" />
                                    </Form.Group>
                                    <Form.Check
                                        type="switch"
                                        id="custom-switch"
                                        label="confirm"
                                    />
                                    <Button variant="primary">Primary</Button>{' '}
                                </Form>

                                {/*<text style={{fontSize:"30px",margin:'80px',fontFamily:'Monospace',color:'#090809'}}>Transfer Money</text>*/}
                                {/*<br/>*/}

                                {/*<input  style={{marginTop:"20px",marginLeft:'90px',paddingLeft:'20px',width: "60%"}} type="number" value={this.state.balance}*/}
                                {/*        onChange={(e) => this.handleChange({ balance: e.target.value })} />*/}
                                {/*<br/>*/}
                                {/*<Button  style={{marginTop:"20px",marginLeft:'160px',width:"30%"}} variant="contained" onClick={this.handleClick}>Submit</Button>*/}
                            </Paper>

                        </Grid>

                    </Grid>

                </Container>
            </div>

        )
    }
}

export default Transfer;
