import React from "react";
import {Col,Row,Card,CardTitle, Button} from "reactstrap"

class Withdraw extends React.Component{
    constructor() {
        super();
        this.state={
            accountNumber:"102",
            balance:''
        }
        this.handleChange=this.handleChange.bind(this)
    }

    handleChange(change){
        this.setState(change)
    }

    render() {
        return(

            <Row style={{paddingTop: '50px',paddingLeft:'50px', align:"center"}} >
                <Col sm="3">
                    <Card body>
                        <CardTitle tag="h5">Withdraw Money</CardTitle>
                        <input type="text" pattern="[0-9]*" value={this.state.balance}
                               onChange={(e) => this.handleChange({ balance: e.target.value })} />
                        <Button style={{color:"white",background:"blue"}}>Withdraw</Button>
                    </Card>
                </Col>
            </Row>
        );
    }

}
export default Withdraw;
