import React from 'react';
import 'antd/dist/antd.css';
import { Modal, Button } from "antd";
import { Form, Select, DatePicker } from "antd";
import AllDetails from "./AllDetails";
import CreditDetails from "./CreditDetails";
import DebitDetails from "./DebitDetails";

class TransactionDetails extends React.Component{

    constructor() {
        super();
        this.state={
            accountNumber:window.localStorage.getItem('accountNumber'),
            option:'all',
            date:'',
            show:null,
            view:false,
            isModalVisible:false

        }

        this.handleClick=this.handleClick.bind(this)
        this.onChangeDate=this.onChangeDate.bind(this)
        this.onChangeOption=this.onChangeOption.bind(this)
        this.showModal=this.showModal.bind(this)
        this.handleCancel=this.handleCancel.bind(this)
        this.handleOk=this.handleOk.bind(this)

    }


    handleClick(){
        this.setState({view:true})
        switch (this.state.option) {
            case 'all':
                this.setState({show:<AllDetails date={this.state.date}/>})
                break;
            case 'credit':
                this.setState({show:<CreditDetails date={this.state.date}/>})
                break;
            default:
                this.setState({show:<DebitDetails date={this.state.date}/>})

        }

    }
    onChangeDate(date,dateString){
        console.log(date,dateString)
        this.setState({date:dateString})
        console.log(this.state.date);
    }
    onChangeOption(value){
        console.log(value)
        this.setState({option:value})
    }

    showModal() {
        this.setState({isModalVisible:true})
    }

    handleOk() {
        this.setState({isModalVisible:false})

    }

    handleCancel (){
        this.setState({isModalVisible:false})
    }

    render()
    {



        return(
            <div>
                <Button type="primary" onClick={showModal}>
                    Open Modal
                </Button>
                <Modal
                    title="Basic Modal"
                    visible={isModalVisible}
                    onOk={handleOk}
                    onCancel={handleCancel}
                >
                    <Form labelCol={{span: 10}} wrapperCol={{span: 16,}} style={{marginTop:"10px"}} name="control-ref" >
                    <Form.Item label="Choose format" >
                        <Select style={{ width: 200 }}  placeholder="Choose details format" onChange={this.onChangeOption}>
                            <Select.Option value="all"> All</Select.Option>
                            <Select.Option value="credit">Credit</Select.Option>
                            <Select.Option value="debit">Debit</Select.Option>
                        </Select>
                    </Form.Item>

                    <Form.Item label="Date">
                        <DatePicker onChange={this.onChangeDate}/>
                    </Form.Item>
                    <Form.Item label="Get Details">
                        <Button onClick={this.handleClick}>Submit</Button>
                    </Form.Item>
                </Form>
                </Modal>
            </div>


        );
    }

}

export default TransactionDetails;
